pub enum AccountError {
    TypedEncoded(TypedEncodedError),
    UnexpectedUserAccount,
    NoAccountForId(OffAccountId),
}

pub enum TypedEncodedError {
    Decode(scale::Error),
    DifferentTypes {
        lhs: core::any::TypeId,
        rhs: core::any::TypeId,
    },
    AlreadyInitialized {
        initialized_id: core::any::TypeId,
        new_id: core::any::TypeId,
    },
    StillUninitialized,
}
